  my ($self, $other) = @_;
  if( UNIVERSAL::isa($other, 'Search::Xapian::PositionIterator') ) {
    Search::Xapianc::PositionIterator_nequal1($self, $other);
  } else {
    ($self+0) != ($other+0);
  }
}

